mapper.update:
UPDATE AlbumReview
rating = #{rating}, comment = #{comment}, reviewed_at = #{reviewedAt},
WHERE review_id = #{reviewId}

mapper.select:
SELECT 
FROM AlbumReview
WHERE fan_id = #{fanId} AND album_id = #{albumId}

SELECT ar.review_id, ar.fan_id, ar.album_id, ar.rating, ar.comment, ar.reviewed_at, ar.created_at, f.name AS fan_name, a.title AS album_title, b.name AS band_name FROM AlbumReview ar LEFT JOIN Fan f ON ar.fan_id = f.fan_id LEFT JOIN Album a ON ar.album_id = a.album_id LEFT JOIN Band b ON a.band_id = b.band_id
AND ar.fan_id = #{fanId}
AND ar.album_id = #{albumId}
AND ar.rating = #{rating}
AND f.name LIKE CONCAT('%', #{fanName}, '%')
AND a.title LIKE CONCAT('%', #{albumTitle}, '%')
ORDER BY ar.reviewed_at DESC

SELECT COUNT(*)
FROM AlbumReview ar
LEFT JOIN Fan f ON ar.fan_id = f.fan_id
LEFT JOIN Album a ON ar.album_id = a.album_id

    
        AND ar.fan_id = #{fanId}
    
    
        AND ar.album_id = #{albumId}
    
    
        AND ar.rating = #{rating}
    
    
        AND f.name LIKE CONCAT('%', #{fanName}, '%')
    
    
        AND a.title LIKE CONCAT('%', #{albumTitle}, '%')
